// src/lib/agents.ts
import { db } from './firebase';
import { collection, query, where, getDocs, onSnapshot } from 'firebase/firestore';

export interface Agent {
  id: string;
  name: string;
  description: string;
  supportedModes: ('text' | 'voice')[];
  tags: string[];
  isTestAgent: boolean;
}

export const getAgents = async (userId: string | null) => {
  const agentsRef = collection(db, 'agents');
  let q;

  if (!userId) {
    // Anonymous users only get test agents
    q = query(agentsRef, where('isTestAgent', '==', true));
  } else {
    // Get all agents for authenticated users with sandbox_access
    q = query(agentsRef);
  }

  const snapshot = await getDocs(q);
  return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as Agent);
};

export const subscribeToAgents = (userId: string | null, callback: (agents: Agent[]) => void) => {
  const agentsRef = collection(db, 'agents');
  let q;

  if (!userId) {
    q = query(agentsRef, where('isTestAgent', '==', true));
  } else {
    q = query(agentsRef);
  }

  return onSnapshot(q, (snapshot) => {
    const agents = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }) as Agent);
    callback(agents);
  });
};
```

