// src/components/TextChatInput.tsx
import { useState, FormEvent, KeyboardEvent, useRef, useEffect } from 'react';
import { Button } from '@keryk-mentor/shared';

interface TextChatInputProps {
  onSendMessage: (message: string) => void;
  disabled?: boolean;
  autoFocus?: boolean;
  maxLength?: number;
}

export function TextChatInput({ 
  onSendMessage, 
  disabled = false, 
  autoFocus = false,
  maxLength = 1000 
}: TextChatInputProps) {
  const [message, setMessage] = useState('');
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Auto-resize textarea as content grows
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [message]);

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();
    if (message.trim() && !disabled) {
      onSendMessage(message.trim());
      setMessage('');
      // Keep focus on textarea after sending
      setTimeout(() => textareaRef.current?.focus(), 0);
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e as unknown as FormEvent);
    }
  };

  const charactersRemaining = maxLength - message.length;
  const isNearLimit = charactersRemaining < maxLength * 0.1;

  return (
    <form 
      onSubmit={handleSubmit}
      className="flex items-center gap-2 p-4 border-t border-border bg-background transition-all duration-200"
    >
      <div className="relative flex-1">
        <textarea
          ref={textareaRef}
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type your message..."
          disabled={disabled}
          maxLength={maxLength}
          autoFocus={autoFocus}
          rows={1}
          style={{ fontSize: '16px' }} // Prevents iOS zoom on focus
          className="w-full min-h-[48px] bg-card rounded-md p-3 text-foreground focus:outline-none focus:ring-1 focus:ring-primary resize-none transition-all duration-200"
        />
        {maxLength && (
          <div className={`text-xs absolute bottom-1 right-2 transition-opacity duration-200 ${isNearLimit ? 'text-warning' : 'text-muted-foreground'} ${message.length > 0 ? 'opacity-100' : 'opacity-0'}`}>
            {charactersRemaining}
          </div>
        )}
      </div>
      <Button 
        type="submit" 
        disabled={!message.trim() || disabled}
        className="h-[48px] w-[48px] flex items-center justify-center transition-all duration-200"
        aria-label="Send message"
      >
        Send
      </Button>
    </form>
  );
}
```

