```typescript
// src/hooks/useAgentChat.ts
import { useState, useCallback, useEffect } from 'react';
import { agentService } from '../services/agentService';

interface Message {
  id: string;
  content: string;
  sender: 'user' | 'agent';
  timestamp: number;
  status?: 'sending' | 'sent' | 'failed';
  metadata?: Record<string, any>;
}

export function useAgentChat(agentId: string) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [offlineQueue, setOfflineQueue] = useState<Message[]>([]);
  const [isOnline, setIsOnline] = useState(typeof navigator !== 'undefined' ? navigator.onLine : true);
  const [isTyping, setIsTyping] = useState(false);
  const [abortController, setAbortController] = useState<AbortController | null>(null);

  // Handle online/offline status
  useEffect(() => {
    const handleOnline = () => setIsOnline(true);
    const handleOffline = () => setIsOnline(false);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Process offline queue when back online
  useEffect(() => {
    if (isOnline && offlineQueue.length > 0 && !isProcessing) {
      // Process queue
      const processQueue = async () => {
        setIsProcessing(true);
        try {
          for (const message of offlineQueue) {
            await sendMessage(message.content);
          }
          setOfflineQueue([]);
        } finally {
          setIsProcessing(false);
        }
      };
      processQueue();
    }
  }, [isOnline, offlineQueue, isProcessing]);

  const retryMessage = useCallback((messageId: string) => {
    const failedMessage = messages.find(msg => msg.id === messageId && msg.status === 'failed');
    if (failedMessage) {
      sendMessage(failedMessage.content);
      // Remove the failed message
      setMessages(prev => prev.filter(msg => msg.id !== messageId));
    }
  }, [messages]);

  const cancelRequest = useCallback(() => {
    if (abortController) {
      abortController.abort();
      setAbortController(null);
      setIsTyping(false);
      setIsProcessing(false);
    }
  }, [abortController]);

  const sendMessage = useCallback(async (content: string) => {
    // Cancel any ongoing request
    if (abortController) {
      abortController.abort();
    }

    const newController = new AbortController();
    setAbortController(newController);

    const userMessage: Message = {
      id: Date.now().toString(),
      content,
      sender: 'user',
      timestamp: Date.now(),
      status: isOnline ? 'sending' : 'failed'
    };

    setMessages(prev => [...prev, userMessage]);

    if (!isOnline) {
      setOfflineQueue(prev => [...prev, userMessage]);
      return;
    }

    setIsProcessing(true);
    setIsTyping(true);

    try {
      // Update user message status to sending
      setMessages(prev => prev.map(msg => 
        msg.id === userMessage.id ? { ...msg, status: 'sending' } : msg
      ));

      const response = await agentService.sendMessage(agentId, content, newController.signal);

      // Update user message status
      setMessages(prev => prev.map(msg => 
        msg.id === userMessage.id ? { ...msg, status: 'sent' } : msg
      ));

      // Add agent response
      setMessages(prev => [...prev, {
        id: Date.now().toString(),
        content: response.message,
        sender: 'agent',
        timestamp: Date.now(),
        metadata: response.metadata
      }]);
    } catch (error) {
      if ((error as Error).name !== 'AbortError') {
        console.error('Error sending message:', error);
        // Update message status to failed
        setMessages(prev => prev.map(msg => 
          msg.id === userMessage.id ? { ...msg, status: 'failed' } : msg
        ));
      }
    } finally {
      setIsTyping(false);
      setIsProcessing(false);
      setAbortController(null);
    }
  }, [agentId, isOnline, abortController]);

  return { 
    messages, 
    sendMessage, 
    isProcessing, 
    isTyping, 
    isOnline, 
    retryMessage,
    cancelRequest
  };
}
```

