```typescript
// src/hooks/useRealtimeVoice.ts
import { useState, useEffect, useCallback } from 'react';

export function useRealtimeVoice(agentId: string) {
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [lastAgentMessage, setLastAgentMessage] = useState('');
  const [error, setError] = useState<string | null>(null);
  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [audioContext, setAudioContext] = useState<AudioContext | null>(null);
  const [mediaStream, setMediaStream] = useState<MediaStream | null>(null);

  // Initialize audio context and request permissions
  const initializeAudio = useCallback(async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const context = new AudioContext();
      setMediaStream(stream);
      setAudioContext(context);
      return true;
    } catch (err) {
      setError('Microphone access denied');
      return false;
    }
  }, []);

  // Start voice chat session
  const startVoiceChat = useCallback(async () => {
    if (!audioContext) {
      const initialized = await initializeAudio();
      if (!initialized) return;
    }

    // Connect to OpenAI Realtime API
    const ws = new WebSocket('wss://api.openai.com/v1/audio/realtime');

    ws.onopen = () => {
      // Send initial configuration
      ws.send(JSON.stringify({
        type: 'config',
        agent_id: agentId,
        encoding: 'linear16',
        sample_rate: 16000
      }));
      setIsListening(true);
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.type === 'transcript') {
        setTranscript(data.text);
      } else if (data.type === 'audio') {
        // Handle incoming audio from agent
        playAudio(data.audio);
        setIsSpeaking(true);
      } else if (data.type === 'message') {
        setLastAgentMessage(data.text);
      } else if (data.type === 'error') {
        setError(data.message);
      } else if (data.type === 'audio_end') {
        setIsSpeaking(false);
      }
    };

    ws.onerror = (err) => {
      setError('Connection error');
      setIsListening(false);
    };

    ws.onclose = () => {
      setIsListening(false);
      setIsSpeaking(false);
    };

    setSocket(ws);

    // Start streaming audio to the server
    startAudioStream(ws);
  }, [agentId, audioContext, initializeAudio]);

  // Start streaming audio from microphone to server
  const startAudioStream = useCallback((ws: WebSocket) => {
    if (!mediaStream || !audioContext) return;

    const source = audioContext.createMediaStreamSource(mediaStream);
    const processor = audioContext.createScriptProcessor(4096, 1, 1);

    processor.onaudioprocess = (e) => {
      if (ws.readyState === WebSocket.OPEN && isListening && !isSpeaking) {
        const audioData = e.inputBuffer.getChannelData(0);
        ws.send(audioData);
      }
    };

    source.connect(processor);
    processor.connect(audioContext.destination);

    return () => {
      source.disconnect();
      processor.disconnect();
    };
  }, [mediaStream, audioContext, isListening, isSpeaking]);

  // Play audio received from the server
  const playAudio = useCallback((audioData: ArrayBuffer) => {
    if (!audioContext) return;

    audioContext.decodeAudioData(audioData, (buffer) => {
      const source = audioContext.createBufferSource();
      source.buffer = buffer;
      source.connect(audioContext.destination);
      source.start(0);

      source.onended = () => {
        setIsSpeaking(false);
      };
    });
  }, [audioContext]);

  // Stop voice chat
  const stopVoiceChat = useCallback(() => {
    if (socket) {
      socket.close();
      setSocket(null);
    }
    setIsListening(false);
    setIsSpeaking(false);
  }, [socket]);

  // Clean up on unmount
  useEffect(() => {
    return () => {
      if (socket) socket.close();
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [socket, mediaStream]);

  return {
    isListening,
    isSpeaking,
    transcript,
    lastAgentMessage,
    error,
    startVoiceChat,
    stopVoiceChat
  };
}
```

