// src/components/ChatInterface.tsx
import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import { Button } from '@keryk-mentor/shared';
import { TextChat } from './TextChat';
import { TextChatInput } from './TextChatInput';
import { VoiceChat } from './VoiceChat';
import { ModeToggle } from './ModeToggle';
import { useAgent } from '@/hooks/useAgent';
import { useMessageQueue } from '@/hooks/useMessageQueue';
import { useRealtimeVoice } from '@/hooks/useRealtimeVoice';

interface ChatInterfaceProps {
  agentId: string;
}

export function ChatInterface({ agentId }: ChatInterfaceProps) {
  const router = useRouter();
  const { agent, isLoading: isAgentLoading } = useAgent(agentId);
  const [mode, setMode] = useState<'text' | 'voice'>('text');
  const { messages, sendMessage, isProcessing } = useMessageQueue(agentId);
  const { 
    isListening, 
    isSpeaking, 
    transcript, 
    lastAgentMessage, 
    error: voiceError, 
    startVoiceChat, 
    stopVoiceChat 
  } = useRealtimeVoice(agentId);

  // Initialize voice chat when switching to voice mode
  useEffect(() => {
    if (mode === 'voice') {
      startVoiceChat();
    } else {
      stopVoiceChat();
    }
  }, [mode, startVoiceChat, stopVoiceChat]);

  // Handle mode change
  const handleModeChange = (newMode: 'text' | 'voice') => {
    if (newMode === mode) return;
    setMode(newMode);
  };

  if (isAgentLoading) {
    return <div className="flex items-center justify-center h-screen">Loading agent...</div>;
  }

  if (!agent) {
    return <div className="flex items-center justify-center h-screen">Agent not found</div>;
  }

  return (
    <div className="flex flex-col h-screen bg-background">
      {/* Header */}
